from screeninfo import get_monitors
from tkinter import Label, Button, Tk
from tkinter import PhotoImage
from threading import Thread, Lock

from cv2 import resize, INTER_LINEAR, matchTemplate, cvtColor, COLOR_BGR2GRAY, TM_CCOEFF_NORMED

from numpy import array, any
from mss import mss
from time import time, sleep
from base64 import b64decode
from PIL import Image
from io import BytesIO
# import datetime

class ScreenMonitor:
    def __init__(self):
        self.running = False
        self.paused = False
        self.count = 0
        self.start_time = None
        self.elapsed_time = 0
        self.lock = Lock()
        self.tmp = ""

    def start(self):
        self.running = True
        self.paused = False
        self.count = 0
        self.start_time = time()
        Thread(target=self.monitor_screen, daemon=True).start()

    def stop(self):
        self.running = False
        if self.start_time:
            self.elapsed_time += time() - self.start_time
            self.start_time = None

    def monitor_screen(self):
        with mss() as sct:
            while self.running:
                if not self.paused:
                    for monitor in get_monitors():
                        width = monitor.width
                        height = monitor.height
                        if width == 1920 and height == 1080:
                            # screen_info = "1k"
                            x1, y1, x2, y2 = 832, 975, 1087, 1050
                        elif width == 2560 and height == 1440:
                            # screen_info = "2k"
                            x1, y1, x2, y2 = 1110, 1300, 1450, 1400
                        elif width == 3840 and height == 2160:
                            # screen_info = "4k"
                            x1, y1, x2, y2 = 1665, 1950, 2175, 2100

                    screen = array(sct.grab({"top": y1, "left": x1, "width": x2 - x1, "height": y2 - y1})) # img1

                    template = array(Image.open(BytesIO(b64decode(self.tmp))))
                    # template = imread('template.png', 0) #img2
                    template_resized = resize(template, (screen.shape[1], screen.shape[0]), interpolation=INTER_LINEAR)
                    result = matchTemplate(cvtColor(screen, COLOR_BGR2GRAY), cvtColor(template_resized, COLOR_BGR2GRAY), TM_CCOEFF_NORMED)

                    if any(result >= 0.95):  # Adjust threshold as needed
                        with self.lock:
                            self.count += 1
                            # filename = datetime.datetime.now().strftime('%Y-%m-%d_%H-%M-%S.png')
                            # imwrite(filename, screen)
                        sleep(8)  # stop checking for 8 seconds
                sleep(1 / 10)  # 30 checks per second

    def get_count(self):
        with self.lock:
            return self.count

    def get_elapsed_time(self):
        with self.lock:
            if self.running and self.start_time:
                return time() - self.start_time
            return self.elapsed_time


class Application(Tk):
    def __init__(self):
        super().__init__()
        self.title("BMW death counter")
        self.geometry("250x350")
        self.resizable(False, False)  # 禁止调整窗口大小
        self.png = ""
        # self.iconbitmap(bitmap="icon.ico")  # 替换为图标路径

        # self.image = PhotoImage(file="icon.png")  # 替换为图片路径
        self.image = PhotoImage(data=self.png)  # 替换为图片路径
        self.iconphoto(self, self.image)
        self.image_label = Label(image=self.image)
        self.image_label.pack(fill='both', expand=True)

        self.monitor = ScreenMonitor()

        self.start_button = Button(self, text="Start", width=10, command=self.start)
        self.end_button = Button(self, text="End", width=10, command=self.end)

        self.start_button.pack(side='left', fill='y')
        self.end_button.pack(side='right', fill='y')

        self.time_label = Label(self, text="Time: 0.0s")
        self.time_label.pack(pady=5)

        self.count_label = Label(self, text="Death Cnt: 0")
        self.count_label.pack(pady=5)

        self.update_gui()

    def start(self):
        self.monitor.start()
        self.update_gui()

    def pause(self):
        self.monitor.pause()
        self.update_gui()

    def end(self):
        self.monitor.stop()
        self.update_gui()

    def update_gui(self):
        if self.monitor.running or self.monitor.paused:
            self.after(100, self.update_gui)
        hours, remainder = divmod(self.monitor.get_elapsed_time(), 3600)
        minutes, seconds = divmod(remainder, 60)
        formatted_time = f"{int(hours):02}:{int(minutes):02}:{int(seconds):02}"

        self.time_label.config(text = "Time: " + formatted_time)
        self.count_label.config(text = f"Death Cnt: {self.monitor.get_count()}")


if __name__ == "__main__":
    app = Application()
    app.mainloop()
